Parameter.Value:
			if (FunctionModel=="Standard")
			{
				write(ConfigOutput02, 0x0000
				| InputMode=="Counter"			? 0x0001 : 0x0000
				| DeliveryValue=="2"			? 0x0008 : 0x0000
				| DeliveryValue=="4"			? 0x0010 : 0x0000
				| MicrostepResolution=="6 Bit"	? 0x0020 : 0x0000
				| MicrostepResolution=="7 Bit"	? 0x0040 : 0x0000
				| MicrostepResolution=="8 Bit"	? 0x0060 : 0x0000
				);

				write(ConfigOutput03, HoldingCurrent);
				write(ConfigOutput04, RatedCurrent);
				write(ConfigOutput05, MaximumCurrent);
				write(ConfigOutput16, MixedDecayConfiguration);

				if(SDCInformation=="on")
				{
					write(ConfigOutput09, 0x00
					| LatchMode=="postive edge R"												? 0x02 : 0x00
					| LatchMode=="negative edge R"												? 0x04 : 0x00
					| LatchSync=="Stepper latch value asynchron (AB/R latch value synchron)"	? 0x08 : 0x00
					);

					write(SdcConfig01, 0x00
					| TriggerEdge01=="falling"			? 0x01 : 0x00
					| SDCSetTimeEnable=="on"			? 0x40 : 0x00
					| SDCInformation=="on"				? 0x80 : 0x00
					);

					if(SDCSetTimeEnable=="on")
					{
						write(DelayedCurrentSwitchOff01, DelayedCurrentSwitchOff01);
					}

					write(MotorSettlingTime01, MotorSettlingTime01);
				}
				else
				{
					write(ConfigOutput09, 0x00
					| LatchEnable=="pos. edge enable Latchfunction AB/R"						? 0x01 : 0x00
					| LatchMode=="postive edge R"												? 0x02 : 0x00
					| LatchMode=="negative edge R"												? 0x04 : 0x00
					| LatchSync=="Stepper latch value asynchron (AB/R latch value synchron)"	? 0x08 : 0x00
					);
				}

				write(ConfigOutput01, StallDetection);
				write(FullStepThreshold01, FullStepThreshold01_conf);
				write(StallDetectMinSpeed01, StallDetectionMinSpeed01);
			}

			if (FunctionModel=="MotionConfiguration")
			{
				write(ConfigOutput02, 0x0000);

				write(ConfigOutput03, HoldingCurrent);
				write(ConfigOutput04, RatedCurrent);
				write(ConfigOutput05, MaximumCurrent);
				write(ConfigOutput16, MixedDecayConfiguration);

				write(ConfigOutput09, 0x00
				| LatchMode=="postive edge R"												? 0x02 : 0x00
				| LatchMode=="negative edge R"												? 0x04 : 0x00
				| LatchSync=="Stepper latch value asynchron (AB/R latch value synchron)"	? 0x08 : 0x00
				);

				write(SdcConfig01, 0xC0
				| TriggerEdge01=="falling"			? 0x01 : 0x00
				);	/* set SDCInformation and SDCSetTimeEnable */

				write(DelayedCurrentSwitchOff01, DelayedCurrentSwitchOff01);

				write(MotorSettlingTime01, MotorSettlingTime01);

				write(ConfigOutput01, StallDetection);
				write(FullStepThreshold01, FullStepThreshold01_conf);
				write(StallDetectMinSpeed01, StallDetectionMinSpeed01);
			}

			if (FunctionModel=="Ramp")
			{
				write(ConfigOutput03a, HoldingCurrent01);
				write(ConfigOutput04a, RatedCurrent01);
				write(ConfigOutput05a, MaximumCurrent01);
				write(FullStepThreshold01, FullStepThreshold01_conf);
				write(MaxSpeed01pos,MaxSpeed01pos_conf);
				write(MaxAcc01,MaxAcc01_conf);
				write(MaxDec01,MaxDec01_conf);
				write(RevLoop01,RevLoop01_conf);
				write(FixedPos01a,FixedPos01a_conf);
				write(FixedPos01b,FixedPos01b_conf);
				write(RefSpeed01,RefSpeed01_conf);
				write(StallRecognitionDelay01,StallRecognitionDelay01_conf);
				write(JoltTime01,JoltTime01_conf);

				if(RefConfig01_selector == "neg. edge")
				{
					write(RefConfig01, 0x82);
				}
				else if(RefConfig01_selector == "pos. edge")
				{
					write(RefConfig01, 0x83);
				}
				else if(RefConfig01_selector == "stall")
				{
					write(RefConfig01, 0x81);
				}
				else if(RefConfig01_selector == "unconditional")
				{
					write(RefConfig01, 0x80);
				}
				else if(RefConfig01_selector == "neg. edge DI4")
				{
					write(RefConfig01, 0x86);
				}
				else if(RefConfig01_selector == "pos. edge DI4")
				{
					write(RefConfig01, 0x87);
				}

				write(StallDetectConfig01, 0x00
				| MixedDecayConfiguration01
				| StallDetection01<<4
				| EnableMotorLoadDisplay=="on"		? 0x80 : 0x00 );

				write(GeneralConfig01, 0x00
				| EnhancedControlword01=="on"		? 0x01 : 0x00
				| MPGenCycleIdx01=="10ms"           ? 0x02 : 0x00
				| MPGenCycleIdx01=="5ms"            ? 0x04 : 0x00 );

				write(LimitSwitchConfig01, 0x00
				| NegLimitSwitch01=="low_active"	? 0x01 : 0x00
				| NegLimitSwitch01=="high_active"	? 0x03 : 0x00
				| PosLimitSwitch01=="low_active"	? 0x04 : 0x00
				| PosLimitSwitch01=="high_active"	? 0x0C : 0x00
				| DirectionControl01=="on"			? 0x80 : 0x00 );

				write(PositionLimitMin01, PositionLimitMin01);
				write(PositionLimitMax01, PositionLimitMax01);
			
				write(StallDetectMinSpeed01, StallDetectionMinSpeed01);
			}

			if (FunctionModel=="CANIOBusController")
			{
				write(ConfigOutput03a, HoldingCurrent01);
				write(ConfigOutput04a, RatedCurrent01);
				write(ConfigOutput05a, MaximumCurrent01);
				write(FullStepThreshold01, FullStepThreshold01_conf);
				write(MaxSpeed01pos,MaxSpeed01pos_conf);
				write(MaxAcc01,MaxAcc01_conf);
				write(MaxDec01,MaxDec01_conf);
				write(RevLoop01,RevLoop01_conf);
				write(FixedPos01a,FixedPos01a_conf);
				write(FixedPos01b,FixedPos01b_conf);
				write(RefSpeed01,RefSpeed01_conf);
				write(StallRecognitionDelay01,StallRecognitionDelay01_conf);
				write(JoltTime01,JoltTime01_conf);

				if(RefConfig01_selector == "neg. edge")
				{
					write(RefConfig01, 0x82);
				}
				else if(RefConfig01_selector == "pos. edge")
				{
					write(RefConfig01, 0x83);
				}
				else if(RefConfig01_selector == "stall")
				{
					write(RefConfig01, 0x81);
				}
				else if(RefConfig01_selector == "unconditional")
				{
					write(RefConfig01, 0x80);
				}
				else if(RefConfig01_selector == "neg. edge DI4")
				{
					write(RefConfig01, 0x86);
				}
				else if(RefConfig01_selector == "pos. edge DI4")
				{
					write(RefConfig01, 0x87);
				}

				write(StallDetectConfig01, 0x00
				| MixedDecayConfiguration01
				| StallDetection01<<4
				| EnableMotorLoadDisplay=="on"		? 0x80 : 0x00 );

				write(GeneralConfig01, 0x00
				| EnhancedControlword01=="on"		? 0x01 : 0x00
				| MPGenCycleIdx01=="10ms"           ? 0x02 : 0x00
				| MPGenCycleIdx01=="5ms"            ? 0x04 : 0x00 );

				write(LimitSwitchConfig01, 0x00
				| NegLimitSwitch01=="low_active"	? 0x01 : 0x00
				| NegLimitSwitch01=="high_active"	? 0x03 : 0x00
				| PosLimitSwitch01=="low_active"	? 0x04 : 0x00
				| PosLimitSwitch01=="high_active"	? 0x0C : 0x00
				| DirectionControl01=="on"			? 0x80 : 0x00 );

				write(PositionLimitMin01, PositionLimitMin01);
				write(PositionLimitMax01, PositionLimitMax01);
			
				write(StallDetectMinSpeed01, StallDetectionMinSpeed01);
			}

write(Configuration01, 0x0000
| LatchConfig01=="postive edge R" ? 0x0001 : 0x0000
| LatchConfig01=="negative edge R" ? 0x0002 : 0x0000
| LatchConfig01=="Latch disabled" ? 0x0003 : 0x0000
| ReferenceEnable01=="Use reference enable 01" ? 0x0004 : 0x0000
| Referencelevel01=="inverted" ? 0x0008 : 0x0000
| SwitchConfig01=="EnableInput_IN3" ? 0x0020 : 0x0000
| SwitchConfig01=="Endswitch 01 use E3" ? 0x0040 : 0x0000
| SwitchConfig01=="Endswitch 01 use E4" ? 0x0080 : 0x0000
| SwitchConfig01=="Endswitch 01 use E3 and E4" ? 0x00C0 : 0x0000
| SwitchLevel01=="Active level inverted" ? 0x0100 : 0x0000
| TriggerInput01=="Trigger 01 use E3" ? 0x0200 : 0x0000
| TriggerInput01=="Trigger 01 use E4" ? 0x0400 : 0x0000
| CurrentShowConfig01=="Current01 on TriggerCtr" ? 0x0800 : 0x0000
| PWMConfig01=="Output01 current control" ? 0x1000 : 0x0000
| PWMConfig01=="BoostAndHold" ? 0x8000 : 0x0000
| DecayConfig01=="Mixed Decay" ? 0x2000 : 0x0000
);
write(Configuration02, 0x0000
| LatchConfig02=="postive edge R" ? 0x0001 : 0x0000
| LatchConfig02=="negative edge R" ? 0x0002 : 0x0000
| LatchConfig02=="Latch disabled" ? 0x0003 : 0x0000
| ReferenceEnable02=="Use reference enable 02" ? 0x0004 : 0x0000
| Referencelevel02=="inverted" ? 0x0008 : 0x0000
| SwitchConfig02=="EnableInput_IN3" ? 0x0020 : 0x0000
| SwitchConfig02=="Endswitch 02 use E7" ? 0x0040 : 0x0000
| SwitchConfig02=="Endswitch 02 use E8" ? 0x0080 : 0x0000
| SwitchConfig02=="Endswitch 02 use E7 and E8" ? 0x00C0 : 0x0000
| SwitchLevel02=="Active level inverted" ? 0x0100 : 0x0000
| TriggerInput02=="Trigger 02 use E7" ? 0x0200 : 0x0000
| TriggerInput02=="Trigger 02 use E8" ? 0x0400 : 0x0000
| CurrentShowConfig02=="Current02 on TriggerCtr" ? 0x0800 : 0x0000
| PWMConfig02=="Output02 current control" ? 0x1000 : 0x0000
| PWMConfig02=="BoostAndHold" ? 0x8000 : 0x0000
| DecayConfig02=="Mixed Decay" ? 0x2000 : 0x0000
);
write(Configuration03, 0x0000
| LatchConfig03=="postive edge R" ? 0x0001 : 0x0000
| LatchConfig03=="negative edge R" ? 0x0002 : 0x0000
| LatchConfig03=="Latch disabled" ? 0x0003 : 0x0000
| ReferenceEnable03=="Use reference enable 03" ? 0x0004 : 0x0000
| Referencelevel03=="inverted" ? 0x0008 : 0x0000
| SwitchConfig03=="EnableInput_IN3" ? 0x0020 : 0x0000
| SwitchConfig03=="Endswitch 03 use E11" ? 0x0040 : 0x0000
| SwitchConfig03=="Endswitch 03 use E12" ? 0x0080 : 0x0000
| SwitchConfig03=="Endswitch 03 use E11 and E12" ? 0x00C0 : 0x0000
| SwitchLevel03=="Active level inverted" ? 0x0100 : 0x0000
| TriggerInput03=="Trigger 03 use E11" ? 0x0200 : 0x0000
| TriggerInput03=="Trigger 03 use E12" ? 0x0400 : 0x0000
| CurrentShowConfig03=="Current03 on TriggerCtr" ? 0x0800 : 0x0000
| PWMConfig03=="Output03 current control" ? 0x1000 : 0x0000
| PWMConfig03=="BoostAndHold" ? 0x8000 : 0x0000
| DecayConfig03=="Mixed Decay" ? 0x2000 : 0x0000
);
write(Configuration04, 0x0000
| LatchConfig04=="postive edge R" ? 0x0001 : 0x0000
| LatchConfig04=="negative edge R" ? 0x0002 : 0x0000
| LatchConfig04=="Latch disabled" ? 0x0003 : 0x0000
| ReferenceEnable04=="Use reference enable 04" ? 0x0004 : 0x0000
| Referencelevel04=="inverted" ? 0x0008 : 0x0000
| SwitchConfig04=="EnableInput_IN3" ? 0x0020 : 0x0000
| SwitchConfig04=="Endswitch 04 use E15" ? 0x0040 : 0x0000
| SwitchConfig04=="Endswitch 04 use E16" ? 0x0080 : 0x0000
| SwitchConfig04=="Endswitch 04 use E15 and E16" ? 0x00C0 : 0x0000
| SwitchLevel04=="Active level inverted" ? 0x0100 : 0x0000
| TriggerInput04=="Trigger 04 use E15" ? 0x0200 : 0x0000
| TriggerInput04=="Trigger 04 use E16" ? 0x0400 : 0x0000
| CurrentShowConfig04=="Current04 on TriggerCtr" ? 0x0800 : 0x0000
| PWMConfig04=="Output04 current control" ? 0x1000 : 0x0000
| PWMConfig04=="BoostAndHold" ? 0x8000 : 0x0000
| DecayConfig04=="Mixed Decay" ? 0x2000 : 0x0000
);
write(ConfigOutput01, DitherAmplitude);
write(ConfigOutput02, DitherFrequency);
